import styled from 'styled-components';

export const ErrorDiv = styled.div`
	font-family: 'Bebas Neue', cursive;
	.row {
		height: 100vh;
	}
	h3 {
		font-size: 7rem;
		letter-spacing: 2px;
		color: #0e4022;
	}
	p {
		font-size: 2.5rem;
		color: #f91944;
	}
	button {
		background-color: #f91944;
		color: #ffffff;
		letter-spacing: 2px;
	}
`;
